import {articleActionTypes} from "./actionTypes";
import {articles} from "../../mockedData";

const initialState = [];

export const articleReducer = (state = initialState, action) => {
    switch (action.type) {
        case articleActionTypes.CREATE_NEW_ARTICLE:
            return [
                ...state,
                {
                    ...action.article,
                    id: Date.now(), // (＠＾◡＾)
                    createdAt: new Date(),
                    user: action.username
                },
            ];
        case articleActionTypes.FETCH_ARTICLES:
            return articles;

        case articleActionTypes.ARTICLE_EDIT:
            return {
                id: action.article.id,
                createdAt: action.article.createdAt, //нужно сохранять исходную дату или...?
                user: action.article.username
            };
        default:
            return state;
    }
};
