import React, {useState} from "react";
import PropTypes from 'prop-types';
import {Button} from "reactstrap";

//CSS-красоты
const firstFieldCSS = {
    color: '#f64a46',
    backgroundColor: '#dedede'
};

const secondFieldCSS = {
    color: '#b00000',
    backgroundColor: '#aaa'
};

function LoginComponent(props) {
    const [formData, setFormData] = useState({
        login: '',
        password: ''
    });

    const handlerChange = (event) => {
        const target = event.target;
        const name = target.name;
        setFormData((prevState) => ({...prevState, [name]: event.target.value}));
    };

    const submitHandler = (event) => {
        props.login(formData);
        event.preventDefault();
    };

    return (
        <form onSubmit={submitHandler}>
            <label style={firstFieldCSS}>
                Login
                <br/>
                <input name="login" type="text" value={FormData.login} onChange={handlerChange}/>
            </label>
            <br/>
            <label style={secondFieldCSS}>
                Password
                <br/>
                <input name="password" type="password" value={FormData.password} onChange={handlerChange}/>
            </label>
            <br/>
            <Button className='btn-warning'>Registration</Button>
        </form>
    );
}

LoginComponent.propTypes = {
    login: PropTypes.func
};

export default LoginComponent;
