/**
 *
 * @param username {string}
 * @param password {string}
 * @returns {Promise<{ data: Record<string, string> }>}
 */
export const register = ({ username, password }) => {
    return new Promise((resolve, reject) => {
        setTimeout(() => {
            const stringUsers = localStorage.getItem('users');
            const users = JSON.parse(stringUsers);

            if (!!users[username]) {
                throw(new Error('User already exists!'));
            }
            const newUser = {
                username,
                id: Date.now(),
                createdAt: (new Date()).toString()
            };

            users[username] = {
                ...newUser,
                password
            }

            localStorage.setItem('users', JSON.stringify(users));

            if (Math.random() > 0.2) {
                resolve({ data: newUser });
            } else {
                throw(new Error('Network error!'));
            }
        }, 1000);
    });
}


export const login = ({ username, password }) => {
    return new Promise((resolve, reject) => {
        const stringUsers = localStorage.getItem('users');
        const users = JSON.parse(stringUsers);

        if (!!users[username]) {
            throw(new Error('No such user found!'));
        }

        const lsCurrentUser = users[username];

        if (lsCurrentUser.password.toString() !== password) {
            throw(new Error('Incorrect password!'));
        }

        setTimeout(() => {
            if (Math.random() > 0.2) {
                resolve({ data: lsCurrentUser });
            } else {
                throw(new Error('Network error!'));
            }
        }, 1000);
    });
};
