import PropTypes from 'prop-types';
import React, {useRef} from "react";
import {setUsername} from "../../ducks/app/actions";
import {store} from "../../store";

function ArticleOverviewFuncComponent({article}) {
    const inputEl = useRef(null);
    //const NeededArticle = articles.find(item => String(item.id) === id)
    //const {title, content, author, createdAt, imageUrl} = NeededArticle;

    const buttonClickHandler = () => {
        const username = inputEl.current.value;
        store.dispatch(setUsername(username));
    }

    return (
        <div>
            <h1>{article.title}</h1>
            {/*<p>{content}</p>*/}
            {/*<h4>Author: {author}</h4>*/}
            {/*<footer>Created at: {createdAt}</footer>*/}
            {/*<img src={imageUrl} alt="Here must be something"/>*/}
            {/*<input type="text" ref={inputEl}/>*/}
            {/*<button onClick={buttonClickHandler}>Click me</button>*/}
            {/*{store.getState().app.username}*/}
        </div>
    );
}

ArticleOverviewFuncComponent.defaultProps = {
    title: 'Name of Article',
    content: 'Empty Content',
    author: 'Who?',
    createdAt: 'No one knows',
    imageUrl: 'https://dwweb.ru/__a-data/img/knopka.png'
};

ArticleOverviewFuncComponent.propTypes = {
    title: PropTypes.string,
    content: PropTypes.string,
    author: PropTypes.string,
    createdAt: PropTypes.string,
    imageUrl: PropTypes.string
}

export default ArticleOverviewFuncComponent;
