import {actionTypes as Type} from "./actionTypes";

const initialState = {
    username: null,
    isLoggedIn: false,
    loading: false,
    error: false,
};

export const STATUS = {
    NOT_REQUESTED: 'notRequested',
    LOADING: 'loading',
    SUCCESS: 'success',
    ERROR: 'error',
}

export const appReducer = (state = initialState, action) => {
    switch (action.type) {
        case Type.LOGIN_REQUEST:
            return {
                status: true,
                error: false
            };
        case Type.LOGIN:
            return {
        ...state,
            isLoggedIn: true,
            loading: false,
            error: false
    };
        case Type.CHANGE_NAME:
            return {
                ...state,
                username: action.username
            };
        case Type.LOGOUT:
            return {
                ...state,
                isLoggedIn: false,
                loading: false,
                error: false
            };
        case Type.LOGIN_ERROR:
            return {
                loading: false,
                error: true
            }
        default:
            return state;
    }
}
