import './App.css';
import React, {useEffect} from "react";
import {BrowserRouter, Route, Switch, Redirect} from 'react-router-dom';
import NavBarComponent from "./components/NavBarComponent";
import ArticleOverviewFuncComponent from "./components/Articles/ArticleOverviewFuncComponent";
import CounterComponent from "./components/CounterComponent";
import LoginComponent from "./components/LoginComponent";
import ArticleFeedItemComponent from "./components/Articles/ArticleFeedItemComponent";
import FeedComponent from "./components/FeedComponent";
import FeedContainer from "./containers/FeedContainer"
import NotificationComponent from "./components/Notifications/NotificationComponent";
import WithOkButton from "./HOCs/WithOkButton";
import NotificationRenderPropsComponent from "./components/Notifications/NotificationRenderPropsComponent";
import AuthLayoutComponent from "./layouts/AuthLayoutComponent";
import CommonLayoutComponent from "./layouts/CommonLayoutComponent";
import {store} from "./store";
import {Provider} from "react-redux";

const fetchUser = () => {
    return new Promise((resolve) => {
        setTimeout(() => {
            resolve({
                username: 'Sasha'
            });
        }, 5000);
    });
};

const article = {
    title: 'Best title',
    author: 'Sasha',
    content: 'Hello very well',
    createdAt: '05.11.2022',
    imageUrl: 'https://library.kissclipart.com/20181123/eaq/kissclipart-dancing-frogs-clipart-toad-true-frog-clip-art-632d544f5fa39535.jpg'
}

const login = ({ username, password }) => {
    alert(`I'm ${username} with ${password}`);
};



//const NotificationHOCcomponent = WithOkButton(NotificationComponent);

function App() {
     const [user, setUser] = React.useState(null);
     const isAuthenticated = store.getState().app.isLoggedIn;

     useEffect(() => {
         async function fetchData() {
             const fetchedUser = await fetchUser();
             setUser(fetchedUser);
         }
         fetchData();
    }, []);

    return (
        <Provider store={store}>
            <Conne>
                <NavBarComponent user={user}/>
                <Switch>
                    <AuthLayoutComponent
                        exact
                        path='/login'
                        component={LoginComponent}
                        isAuthenticated={isAuthenticated}
                    />
                    <CommonLayoutComponent
                        exact
                        path='/feed'
                        component={FeedContainer}
                        isAuthenticated={isAuthenticated}
                    />
                    <CommonLayoutComponent
                        exact
                        path='/article/:id'
                        component={ArticleOverviewFuncComponent}
                        isAuthenticated={isAuthenticated}
                    />

                </Switch>
            </Conne>
        </Provider>
    );
}

export default App;
