import {actionTypes as Type} from "./actionTypes";

const initialState = {
    isLoggedIn: false
};

export const reducer = (state = initialState, action) => {
    switch (action.type) {
        case Type.LOGIN:
            return {
                ...state,
                user: {
                    username: action.payload.username
                }
            };
        case Type.LOGOUT:
            return {
                ...state,
                user: null
            }
        default:
            return state;
    }
}
