import React, {useState} from "react";
import {Link} from "react-router-dom";

import {
    Button,
    Nav,
    Navbar,
    NavbarBrand,
    NavItem, NavLink
} from "reactstrap";
import PropTypes from "prop-types";
import {store} from "../store";
import {loginAction, logoutAction} from "../store/actions";

function NavBarComponent(props) {

    const currentState = store.getState().isLoggedIn; //<---update (◕‿◕)

    const handleLoginClick = () => {
        store.dispatch(loginAction());
    };

    const handleLogoutClick = () => {
        store.dispatch(logoutAction());
    };

    const superClickButton = currentState
        ? <NavLink><button onClick={handleLogoutClick}>Logout</button></NavLink>
        : <Link to='/login'><button onClick={handleLoginClick}>Login</button></Link>;

    return (
        <Navbar color="light" light>
            <Link to='/feed'>
                {/*<NavbarBrand className="text-decoration-underline">*/}
                    PrivetApp
                {/*</NavbarBrand>*/}
            </Link>
            <Nav className="ml-auto" navbar>
                <NavItem className="text-secondary navbar-text">
                    <Link to={'/feed'}>
                        Feed
                    </Link>
                </NavItem>
                {props.user?.username && (
                    <NavItem className="text-secondary navbar-text">
                        {props.user?.username}
                    </NavItem>
                )}
                {superClickButton}
            </Nav>
        </Navbar>
    );
}

NavBarComponent.propTypes = {
    user: PropTypes.shape({username: PropTypes.string})
}

export default NavBarComponent;
