import {actionTypes as Type} from "./actionTypes";

const initialState = {
    username: null,
    isLoggedIn: false
};

export const appReducer = (state = initialState, action) => {
    switch (action.type) {
        case Type.LOGIN:
            return {
                ...state,
                isLoggedIn: true
            };
        case Type.CHANGE_NAME:
            return {
                ...state,
                username: action.username
            };
        case Type.LOGOUT:
            return {
                ...state,
                isLoggedIn: false
            };
        case Type.ERROR:
            return {

            }
        default:
            return state;
    }
}
